import React, { useState, useEffect } from 'react';
import './App.css';

// Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <nav className="nav">
          <a href="#home" className="logo">Amborella Organics</a>
          <ul className="nav-links">
            <li><a href="#shop">Shop</a></li>
            <li><a href="#grow">Grow</a></li>
            <li><a href="#wholesale">Wholesale</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
          <a href="#cart" className="cart-icon">üõí 0</a>
        </nav>
      </div>
    </header>
  );
};

// Hero Section Component
const Hero = () => {
  return (
    <section className="hero" id="home">
      <div className="container">
        <div className="hero-content fade-in-up">
          <div className="hero-text">
            <h1>Shop Blooming Lollipops!</h1>
            <p className="subtitle">The world's first seed-bearing lollipops that grow into life</p>
            <p>
              Discover our organic candy with edible herbs and flowers that weave throughout. 
              After devouring, plant the biodegradable stick in soil and watch it grow into 
              beautiful herbs and flowers!
            </p>
            <div className="hero-buttons">
              <a href="#shop" className="btn btn-primary">Shop Now</a>
              <a href="#about" className="btn btn-outline">Learn More</a>
            </div>
          </div>
          <div className="hero-image">
            <div style={{
              width: '400px',
              height: '400px',
              background: 'linear-gradient(135deg, #f9c74f, #f4a688, #b19cd9)',
              borderRadius: '20px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '6rem',
              color: 'white',
              boxShadow: 'var(--card-shadow)',
              position: 'relative',
              overflow: 'hidden'
            }}>
              üç≠
              <div style={{
                position: 'absolute',
                top: '20px',
                right: '20px',
                fontSize: '2rem'
              }}>üå±</div>
              <div style={{
                position: 'absolute',
                bottom: '20px',
                left: '20px',
                fontSize: '2rem'
              }}>üå∏</div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// About Section Component
const About = () => {
  return (
    <section className="about" id="about">
      <div className="container">
        <div className="about-content">
          <div className="about-text">
            <h2>Amborella Organics</h2>
            <p>
              At Amborella Organics we've created the world's first and only seed-bearing lollipop.
            </p>
            <p>
              A seed-bearing lollipop is an organic candy with edible herbs and flowers that weave 
              throughout. After devouring, you can plant the biodegradable stick in soil horizontally, 
              cover with a layer of top soil, water daily, and grow a herb or flower.
            </p>
            <p>
              Each heirloom herb or flower grown has a connection to what you just ate; Sage & 
              Marshmallow grows a sage plant, while Peach & Marigold yields marigold.
            </p>
          </div>
          <div className="about-visual">
            <div style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '2rem',
              height: '400px'
            }}>
              <div style={{
                background: 'linear-gradient(135deg, #7fb069, #a8c09a)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '4rem',
                color: 'white'
              }}>üç≠</div>
              <div style={{
                background: 'linear-gradient(135deg, #f4a688, #f9c74f)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '4rem',
                color: 'white'
              }}>üå±</div>
              <div style={{
                background: 'linear-gradient(135deg, #b19cd9, #5c4b8c)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '4rem',
                color: 'white'
              }}>üåø</div>
              <div style={{
                background: 'linear-gradient(135deg, #f9c74f, #d4af37)',
                borderRadius: '12px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '4rem',
                color: 'white'
              }}>üå∏</div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// Featured Products Component
const FeaturedProducts = () => {
  const products = [
    {
      name: "Sage & Marshmallow",
      description: "Earthy sage flavors with sweet marshmallow, grows sage plants",
      price: "$12.99",
      emoji: "üåø",
      color: "linear-gradient(135deg, #7fb069, #a8c09a)"
    },
    {
      name: "Peach & Marigold",
      description: "Sweet peach with floral notes, grows beautiful marigold flowers",
      price: "$12.99",
      emoji: "üåº",
      color: "linear-gradient(135deg, #f4a688, #f9c74f)"
    },
    {
      name: "Lavender & Lemongrass",
      description: "Calming lavender with citrus lemongrass, grows lavender plants",
      price: "$12.99",
      emoji: "üíú",
      color: "linear-gradient(135deg, #b19cd9, #5c4b8c)"
    }
  ];

  return (
    <section className="products" id="shop">
      <div className="container">
        <h2>Featured Products</h2>
        <div className="products-grid">
          {products.map((product, index) => (
            <div key={index} className="product-card fade-in-up">
              <div 
                className="product-image" 
                style={{ background: product.color }}
              >
                <span style={{ fontSize: '4rem' }}>{product.emoji}</span>
              </div>
              <div className="product-info">
                <h3 className="product-title">{product.name}</h3>
                <p className="product-description">{product.description}</p>
                <p className="product-price">{product.price}</p>
                <a href="#buy" className="btn btn-primary">Add to Cart</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Press Section Component
const Press = () => {
  const pressItems = [
    {
      outlet: "NowThis News",
      stats: "9.9 Million Views",
      quote: "Revolutionary lollipops that grow into plants are taking the world by storm!"
    },
    {
      outlet: "Oprah",
      quote: "Sure, these delightful lollipops, which come in a variety of creative flavor combinations will satisfy any sugar craving. But the real reason we think they're pretty sweet? The biodegradable sticks are embedded with sage, lavender, and marigold, so they're designed to be planted."
    },
    {
      outlet: "Forbes",
      quote: "Not surprisingly, Amborella was recently selected as a finalist for the ConsiderBeyond sustainability awards. The eco-conscious brand was also recently handpicked by Reese Witherspoon for her book club."
    }
  ];

  return (
    <section className="press">
      <div className="container">
        <h2>Press</h2>
        <div className="press-grid">
          {pressItems.map((item, index) => (
            <div key={index} className="press-item">
              <div className="press-logo">{item.outlet}</div>
              {item.stats && <div className="press-stats">{item.stats}</div>}
              <p className="press-quote">"{item.quote}"</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Shop by Herb Section Component
const ShopByHerb = () => {
  const herbs = [
    {
      name: "Lavender & Lemongrass",
      description: "Grow Lavender!",
      emoji: "üíú",
      color: "linear-gradient(135deg, #b19cd9, #5c4b8c)"
    },
    {
      name: "Sage & Marshmallow",
      description: "Grow Sage!",
      emoji: "üåø",
      color: "linear-gradient(135deg, #7fb069, #a8c09a)"
    },
    {
      name: "Peach & Marigold",
      description: "Grow Marigold!",
      emoji: "üåº",
      color: "linear-gradient(135deg, #f4a688, #f9c74f)"
    },
    {
      name: "Mint & Hibiscus",
      description: "Grow Mint!",
      emoji: "üå±",
      color: "linear-gradient(135deg, #7fb069, #f4a688)"
    }
  ];

  return (
    <section className="shop-herbs" id="grow">
      <div className="container">
        <h2>Shop by Herb/Flower</h2>
        <div className="herbs-grid">
          {herbs.map((herb, index) => (
            <div key={index} className="herb-card">
              <div 
                className="herb-image"
                style={{ background: herb.color }}
              >
                <span>{herb.emoji}</span>
              </div>
              <div className="herb-info">
                <h3 className="herb-name">{herb.name}</h3>
                <p className="herb-description">{herb.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Newsletter Component
const Newsletter = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Thank you for subscribing with email: ${email}`);
    setEmail('');
  };

  return (
    <section className="newsletter">
      <div className="container">
        <h3>Stay Connected with Nature</h3>
        <p>Get updates on new flavors and growing tips!</p>
        <form className="newsletter-form" onSubmit={handleSubmit}>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email address..."
            required
          />
          <button type="submit" className="btn">Subscribe</button>
        </form>
      </div>
    </section>
  );
};

// Footer Component
const Footer = () => {
  return (
    <footer className="footer" id="contact">
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>Navigation</h3>
            <ul>
              <li><a href="#home">Home</a></li>
              <li><a href="#shop">Shop</a></li>
              <li><a href="#grow">Grow</a></li>
              <li><a href="#wholesale">Wholesale</a></li>
              <li><a href="#about">About</a></li>
              <li><a href="#contact">Contact</a></li>
            </ul>
          </div>
          <div className="footer-section">
            <h3>Customer Care</h3>
            <ul>
              <li><a href="#shipping">Shipping Policy</a></li>
              <li><a href="#refund">Refund Policy</a></li>
              <li><a href="#terms">Terms of Service</a></li>
              <li><a href="#retailers">Retailers</a></li>
              <li><a href="#press">Press</a></li>
            </ul>
          </div>
          <div className="footer-section">
            <h3>Contact Us</h3>
