import React, { useState, useEffect } from 'react';
import './App.css';

// Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <nav className="nav">
          <a href="#home" className="logo">Amborella Organics</a>
          <ul className="nav-links">
            <li><a href="#shop">Shop</a></li>
            <li><a href="#grow">Grow</a></li>
            <li><a href="#wholesale">Wholesale</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
          <a href="#cart" className="cart-icon">🛒 0</a>
        </nav>
      </div>
    </header>
  );
};

// Hero Section Component
const Hero = () => {
  return (
    <section className="hero" id="home">
      <div className="hero-content fade-in-up">
        <div className="hero-text">
          <h1 style={{ color: 'var(--pearl-white)' }}>
            A Sweet Promise <br/>to Future Gardens
          </h1>
          <p className="hero-subtitle" style={{ color: 'rgba(255, 255, 255, 0.9)' }}>
            Share a moment. Plant a memory. Grow a legacy together.
          </p>
          <p className="hero-description" style={{ color: 'rgba(255, 255, 255, 0.85)' }}>
            Picture this: You hand your child an artisanal lollipop, rich with organic herbs and flowers. 
            You both savor the sweetness, then together you plant the biodegradable stick in your garden. 
            Months later, you're both marveling at the lavender or sage you grew together—a living reminder 
            of that perfect shared moment, now blooming for generations to come.
          </p>
          <div className="hero-buttons">
            <a href="#shop" className="btn btn-ghost">Start Our Tradition</a>
            <a href="#about" className="btn btn-outline" style={{ 
              borderColor: 'rgba(255, 255, 255, 0.4)', 
              color: 'rgba(255, 255, 255, 0.9)' 
            }}>
              See the Magic
            </a>
          </div>
        </div>
        <div className="hero-visual">
          <div className="story-sequence">
            <div className="story-moment">
              <div className="story-icon">👨‍👩‍👧‍👦</div>
              <div className="story-label">Share Together</div>
            </div>
            <div className="story-moment">
              <div className="story-icon">🍭</div>
              <div className="story-label">Savor Sweetness</div>
            </div>
            <div className="story-moment">
              <div className="story-icon">🌱</div>
              <div className="story-label">Plant Dreams</div>
            </div>
            <div className="story-moment">
              <div className="story-icon">🌸</div>
              <div className="story-label">Grow Legacy</div>
            </div>
            <div className="story-connecting-lines"></div>
          </div>
        </div>
      </div>
    </section>
  );
};

// About Section Component
const About = () => {
  return (
    <section className="about" id="about">
      <div className="container-narrow">
        <div className="about-content">
          <div className="about-text">
            <h2>From Our Family to Yours</h2>
            <p className="text-luxury">
              Every great tradition starts with a single, perfect moment shared between people who love each other.
            </p>
            <p>
              We created Amborella because we believe that the sweetest experiences are the ones that keep giving. 
              When you unwrap one of our organic lollipops—carefully crafted with heirloom herbs and edible 
              flowers—you're not just treating yourself or someone you love. You're beginning a story that will 
              bloom long after the last taste fades.
            </p>
            <p>
              The magic happens when you take that biodegradable stick, embed it gently in your garden soil, 
              and watch together as the embedded seeds awaken. Your child's eyes light up as tiny green shoots 
              push through the earth. Your grandchildren will one day sit in the shade of what you planted together, 
              understanding that love grows in the most unexpected ways.
            </p>
            <p className="text-large">
              This is how memories become <em>heirlooms</em>. This is how a simple sweet becomes a 
              <em>family legacy</em>, rooted in the earth and reaching toward tomorrow.
            </p>
          </div>
          <div className="about-visual">
            <div style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '3rem',
              height: '480px'
            }}>
              <div style={{
                background: 'radial-gradient(circle at 30% 30%, var(--sage-luxury), var(--emerald-rich))',
                borderRadius: 'var(--border-radius-large)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '5rem',
                color: 'var(--pearl-white)',
                boxShadow: 'var(--shadow-medium)',
                position: 'relative',
                overflow: 'hidden'
              }}>
                🍭
                <div style={{
                  position: 'absolute',
                  bottom: '1rem',
                  right: '1rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  letterSpacing: '0.1em',
                  textTransform: 'uppercase'
                }}>Taste</div>
              </div>
              <div style={{
                background: 'radial-gradient(circle at 70% 70%, var(--marigold-gold), var(--rose-gold))',
                borderRadius: 'var(--border-radius-large)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '5rem',
                color: 'var(--pearl-white)',
                boxShadow: 'var(--shadow-medium)',
                position: 'relative',
                overflow: 'hidden'
              }}>
                🌱
                <div style={{
                  position: 'absolute',
                  bottom: '1rem',
                  right: '1rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  letterSpacing: '0.1em',
                  textTransform: 'uppercase'
                }}>Plant</div>
              </div>
              <div style={{
                background: 'radial-gradient(circle at 40% 60%, var(--amethyst), var(--deep-plum))',
                borderRadius: 'var(--border-radius-large)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '5rem',
                color: 'var(--pearl-white)',
                boxShadow: 'var(--shadow-medium)',
                position: 'relative',
                overflow: 'hidden'
              }}>
                🌿
                <div style={{
                  position: 'absolute',
                  bottom: '1rem',
                  right: '1rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  letterSpacing: '0.1em',
                  textTransform: 'uppercase'
                }}>Grow</div>
              </div>
              <div style={{
                background: 'radial-gradient(circle at 60% 40%, var(--luxury-gold), var(--forest-deep))',
                borderRadius: 'var(--border-radius-large)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '5rem',
                color: 'var(--pearl-white)',
                boxShadow: 'var(--shadow-medium)',
                position: 'relative',
                overflow: 'hidden'
              }}>
                🌸
                <div style={{
                  position: 'absolute',
                  bottom: '1rem',
                  right: '1rem',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  letterSpacing: '0.1em',
                  textTransform: 'uppercase'
                }}>Share</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// Featured Products Component
const FeaturedProducts = () => {
  const products = [
    {
      name: "Sage & Marshmallow",
      description: "Remember grandmother's kitchen garden? This gentle blend captures that timeless wisdom—earthy sage from her herb patch, sweetened with marshmallow's comforting embrace. Share this moment, then plant your memories together.",
      heritage: "Your children will grow Mediterranean sage",
      price: "$18",
      emoji: "🌿",
      color: "radial-gradient(circle at 25% 25%, var(--sage-luxury), var(--emerald-rich))",
      story: "A wisdom that blooms across generations"
    },
    {
      name: "Peach & Marigold",
      description: "Like summer afternoons spent picking fruit with little hands in yours. Sweet peach mingles with marigold's golden sunshine—a flavor that feels like childhood, tastes like joy, and grows into beautiful tomorrows.",
      heritage: "Your garden will burst with golden marigolds",
      price: "$18",
      emoji: "🌼",
      color: "radial-gradient(circle at 75% 25%, var(--rose-gold), var(--marigold-gold))",
      story: "Sunshine captured, memories planted"
    },
    {
      name: "Lavender & Lemongrass",
      description: "For quiet evening moments when you want to slow time. Calming lavender meets bright lemongrass—a flavor that soothes, a tradition that nurtures, a legacy that blooms in purple splendor year after year.",
      heritage: "Aromatic lavender will grace your home",
      price: "$18",
      emoji: "💜",
      color: "radial-gradient(circle at 50% 75%, var(--amethyst), var(--deep-plum))",
      story: "Peace planted, serenity shared"
    }
  ];

  return (
    <section className="products" id="shop">
      <div className="container">
        <div className="text-center" style={{ marginBottom: '4rem' }}>
          <h2>Three Moments, Endless Memories</h2>
          <p className="text-luxury" style={{ maxWidth: '680px', margin: '0 auto' }}>
            Each flavor tells a story, creates a connection, and plants a future. Choose the one that speaks 
            to your family's heart—because the sweetest traditions are the ones that keep growing.
          </p>
        </div>
        <div className="products-grid">
          {products.map((product, index) => (
            <div key={index} className="product-card fade-in-up">
              <div 
                className="product-image" 
                style={{ 
                  background: product.color,
                  height: '320px',
                  borderRadius: 'var(--border-radius-large)',
                  position: 'relative',
                  overflow: 'hidden'
                }}
              >
                <span style={{ 
                  fontSize: '6rem',
                  position: 'relative',
                  zIndex: 2
                }}>{product.emoji}</span>
                <div style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  bottom: 0,
                  background: 'linear-gradient(45deg, rgba(255,255,255,0.1) 0%, transparent 50%, rgba(255,255,255,0.05) 100%)',
                  zIndex: 1
                }}></div>
              </div>
              <div className="product-info" style={{ padding: '2.5rem' }}>
                <h3 className="product-title" style={{ marginBottom: '1rem' }}>{product.name}</h3>
                <p className="product-description" style={{ 
                  fontSize: '1rem',
                  lineHeight: '1.7',
                  marginBottom: '1.5rem',
                  minHeight: '4.5rem'
                }}>{product.description}</p>
                <p style={{ 
                  fontSize: '0.875rem',
                  fontStyle: 'italic',
                  color: 'var(--forest-deep)',
                  marginBottom: '1rem'
                }}>{product.heritage}</p>
                <p style={{
                  fontSize: '0.8rem',
                  fontFamily: 'var(--font-display)',
                  fontStyle: 'italic',
                  color: 'var(--luxury-gold)',
                  marginBottom: '2rem',
                  letterSpacing: '0.02em'
                }}>{product.story}</p>
                <div style={{ 
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '2rem'
                }}>
                  <span className="product-price" style={{ 
                    fontSize: '1.5rem',
                    fontWeight: '300',
                    fontFamily: 'var(--font-display)'
                  }}>{product.price}</span>
                  <span style={{ 
                    fontSize: '0.75rem',
                    textTransform: 'uppercase',
                    letterSpacing: '0.1em',
                    color: 'var(--smoke)'
                  }}>Single Piece</span>
                </div>
                <a href="#buy" className="btn btn-luxury" style={{ width: '100%' }}>Select This Experience</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Press Section Component
const Press = () => {
  const pressItems = [
    {
      outlet: "NowThis News",
      stats: "9.9 Million Views",
      quote: "Revolutionary lollipops that grow into plants are taking the world by storm—proving that the most beautiful innovations often come from the simplest, most profound ideas.",
      accent: "🌍"
    },
    {
      outlet: "Oprah Daily",
      quote: "Sure, these delightful lollipops, which come in a variety of creative flavor combinations will satisfy any sugar craving. But the real reason we think they're pretty sweet? The biodegradable sticks are embedded with sage, lavender, and marigold, so they're designed to be planted.",
      accent: "✨"
    },
    {
      outlet: "Forbes",
      quote: "Not surprisingly, Amborella was recently selected as a finalist for the ConsiderBeyond sustainability awards. The eco-conscious brand was also recently handpicked by Reese Witherspoon for her book club.",
      accent: "🏆"
    }
  ];

  return (
    <section className="press">
      <div className="container">
        <div className="text-center" style={{ marginBottom: '4rem' }}>
          <h2>Global Recognition</h2>
          <p className="text-luxury" style={{ 
            color: 'rgba(255, 255, 255, 0.9)', 
            maxWidth: '600px', 
            margin: '0 auto' 
          }}>
            When you create something truly meaningful, the world takes notice. 
            Our story has touched hearts and inspired minds across the globe.
          </p>
        </div>
        <div className="press-grid">
          {pressItems.map((item, index) => (
            <div key={index} className="press-item">
              <div style={{ 
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                marginBottom: '2rem'
              }}>
                <div className="press-logo">{item.outlet}</div>
                <div style={{ fontSize: '2rem' }}>{item.accent}</div>
              </div>
              {item.stats && (
                <div className="press-stats" style={{ marginBottom: '2rem' }}>
                  {item.stats}
                </div>
              )}
              <p className="press-quote">"{item.quote}"</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Shop by Herb Section Component
const ShopByHerb = () => {
  const herbs = [
    {
      name: "Lavender & Lemongrass",
      description: "Grow Lavender!",
      emoji: "💜",
      color: "linear-gradient(135deg, #b19cd9, #5c4b8c)"
    },
    {
      name: "Sage & Marshmallow",
      description: "Grow Sage!",
      emoji: "🌿",
      color: "linear-gradient(135deg, #7fb069, #a8c09a)"
    },
    {
      name: "Peach & Marigold",
      description: "Grow Marigold!",
      emoji: "🌼",
      color: "linear-gradient(135deg, #f4a688, #f9c74f)"
    },
    {
      name: "Mint & Hibiscus",
      description: "Grow Mint!",
      emoji: "🌱",
      color: "linear-gradient(135deg, #7fb069, #f4a688)"
    }
  ];

  return (
    <section className="shop-herbs" id="grow">
      <div className="container">
        <h2>Shop by Herb/Flower</h2>
        <div className="herbs-grid">
          {herbs.map((herb, index) => (
            <div key={index} className="herb-card">
              <div 
                className="herb-image"
                style={{ background: herb.color }}
              >
                <span>{herb.emoji}</span>
              </div>
              <div className="herb-info">
                <h3 className="herb-name">{herb.name}</h3>
                <p className="herb-description">{herb.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

// Newsletter Component
const Newsletter = () => {
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Welcome to our garden community, ${email}! You'll receive exclusive updates about new confections and growing wisdom.`);
    setEmail('');
  };

  return (
    <section className="newsletter">
      <div className="container-narrow">
        <div className="text-center">
          <h3>Join Our Garden Community</h3>
          <p style={{ 
            fontSize: '1.125rem',
            marginBottom: '3rem',
            color: 'var(--primary-dark)',
            fontWeight: '300'
          }}>
            Be the first to discover new artisanal flavors, receive exclusive growing guides, 
            and join a community dedicated to cultivating both taste and sustainability.
          </p>
          <form className="newsletter-form" onSubmit={handleSubmit}>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email address"
              required
            />
            <button type="submit" className="btn btn-primary">Join the Garden</button>
          </form>
          <p style={{ 
            fontSize: '0.875rem',
            marginTop: '1.5rem',
            color: 'var(--forest-deep)',
            opacity: '0.8'
          }}>
            We respect your privacy and share your passion for the earth. Unsubscribe anytime.
          </p>
        </div>
      </div>
    </section>
  );
};

// Footer Component
const Footer = () => {
  return (
    <footer className="footer" id="contact">
      <div className="container">
        <div className="footer-content">
          <div className="footer-section">
            <h3>Navigation</h3>
            <ul>
              <li><a href="#home">Home</a></li>
              <li><a href="#shop">Shop</a></li>
              <li><a href="#grow">Grow</a></li>
              <li><a href="#wholesale">Wholesale</a></li>
              <li><a href="#about">About</a></li>
              <li><a href="#contact">Contact</a></li>
            </ul>
          </div>
          <div className="footer-section">
            <h3>Customer Care</h3>
            <ul>
              <li><a href="#shipping">Shipping Policy</a></li>
              <li><a href="#refund">Refund Policy</a></li>
              <li><a href="#terms">Terms of Service</a></li>
              <li><a href="#retailers">Retailers</a></li>
              <li><a href="#press">Press</a></li>
            </ul>
          </div>
          <div className="footer-section">
            <h3>Contact Us</h3>
            <p>📍 Laguna Beach, CA 92651 USA</p>
            <p>📞 (949) 306-0620</p>
            <p>✉️ taylor@amborellaorganics.com</p>
            <div className="social-links">
              <a href="#facebook">📘</a>
              <a href="#twitter">🐦</a>
              <a href="#instagram">📷</a>
              <a href="#pinterest">📌</a>
              <a href="#linkedin">💼</a>
              <a href="#vimeo">🎬</a>
            </div>
          </div>
          <div className="footer-section">
            <h3>Our Philosophy</h3>
            <p>
              We believe that true luxury lies not in what we take from the earth, but in what we give back. 
              Each confection is a small act of hope—a promise that indulgence and responsibility can coexist 
              beautifully, creating gardens where there were once only moments.
            </p>
          </div>
        </div>
        <div className="footer-bottom">
          <p>© 2025 Amborella Organics. All Rights Reserved.</p>
          <p>
            Please Note: UPS Next Day Air is our only money-back guaranteed service. 
            We cannot be responsible for packages that go missing, arrive damaged, 
            or late due USPS. Thank you!
          </p>
        </div>
      </div>
    </footer>
  );
};

// Main App Component
function App() {
  return (
    <div className="App">
      <Header />
      <Hero />
      <About />
      <FeaturedProducts />
      <Press />
      <ShopByHerb />
      <Newsletter />
      <Footer />
    </div>
  );
}

export default App;
