import React, { useState, useEffect } from 'react';
import './App.css';

// Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}> 
      <div className="container" style={{ maxWidth: '1400px' }}>
        <nav className="nav">
          <a href="#home" className="logo">Amborella</a>
          <ul className="nav-links">
            <li><a href="#shop">Shop</a></li>
            <li><a href="#grow">Grow</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

// SVG Lollipop Component
const SVGLollipop = ({ style }) => (
  <div className="svg-lollipop" style={style}>
    <svg width="20" height="60" viewBox="0 0 20 60">
      {/* Square lollipop head with plant specks */}
      <rect x="2" y="0" width="16" height="16" rx="2" fill="rgba(212, 165, 116, 0.8)" stroke="rgba(255,255,255,0.3)" strokeWidth="1"/>
      {/* Plant specks inside */}
      <circle cx="6" cy="4" r="1" fill="#1a4b3a" opacity="0.6"/>
      <circle cx="12" cy="6" r="0.8" fill="#3d5a47" opacity="0.7"/>
      <circle cx="8" cy="10" r="1.2" fill="#255c3f" opacity="0.5"/>
      <circle cx="14" cy="12" r="0.9" fill="#1a4b3a" opacity="0.6"/>
      {/* White stick */}
      <rect x="9" y="16" width="2" height="44" fill="#ffffff" rx="1"/>
    </svg>
  </div>
);

// Hero Section with Products Below H1
const Hero = () => {
  const [lollipopDrops, setLollipopDrops] = useState([]);
  const [plants, setPlants] = useState([]);
  const [dropCounter, setDropCounter] = useState(0);

  const products = [
    {
      title: "Frida Kahlo Watermelon 3-Pack",
      price: "$7.50",
      image: "https://via.placeholder.com/200x140/ff6b6b/ffffff?text=Frida+Kahlo"
    },
    {
      title: "Garden Lover's 8 Pack", 
      price: "$20.00",
      image: "https://via.placeholder.com/200x140/4ecdc4/ffffff?text=Garden+Pack"
    },
    {
      title: "Watering Can-dy 20 Pack",
      price: "$50.00", 
      image: "https://via.placeholder.com/200x140/45b7d1/ffffff?text=Can-dy+20"
    }
  ];

  // Periodic lollipop drop effect
  useEffect(() => {
    const dropInterval = setInterval(() => {
      const newDrop = {
        id: dropCounter,
        left: Math.random() * 80 + 10,
        animationDelay: 0
      };

      setLollipopDrops(prev => [...prev, newDrop]);
      setDropCounter(prev => prev + 1);

      // Remove the lollipop after animation and add a plant
      setTimeout(() => {
        setLollipopDrops(prev => prev.filter(drop => drop.id !== newDrop.id));

        const newPlant = {
          id: newDrop.id,
          left: newDrop.left
        };
        setPlants(prev => [...prev, newPlant]);

        // Remove plant after it's fully grown
        setTimeout(() => {
          setPlants(prev => prev.filter(plant => plant.id !== newPlant.id));
        }, 4000);
      }, 2500);
    }, 3000); // Drop every 3 seconds

    return () => clearInterval(dropInterval);
  }, [dropCounter]);

