import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';

// Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}> 
      <div className="container" style={{ maxWidth: '1400px' }}>
        <nav className="nav">
          <a href="#home" className="logo">Amborella</a>
          <ul className="nav-links">
            <li><a href="#shop">Shop</a></li>
            <li><a href="#grow">Grow</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

// New Hero Section with Deep Leaf Plumage
const Hero = () => {
  const [lollipopDrops, setLollipopDrops] = useState([]);
  const [plants, setPlants] = useState([]);
  const [dropCounter, setDropCounter] = useState(0);

  // Create floating leaves
  const FloatingLeaves = () => {
    return (
      <div className="floating-leaves">
        {[...Array(5)].map((_, index) => (
          <div key={index} className="floating-leaf" />
        ))}
      </div>
    );
  };

  // Periodic lollipop drop effect
  useEffect(() => {
    const dropInterval = setInterval(() => {
      const newDrop = {
        id: dropCounter,
        left: Math.random() * 80 + 10, // Random position between 10% and 90%
        animationDelay: 0
      };

      setLollipopDrops(prev => [...prev, newDrop]);
      setDropCounter(prev => prev + 1);

      // Remove the lollipop after animation and add a plant
      setTimeout(() => {
        setLollipopDrops(prev => prev.filter(drop => drop.id !== newDrop.id));

        const newPlant = {
          id: newDrop.id,
          left: newDrop.left
        };
        setPlants(prev => [...prev, newPlant]);

        // Remove plant after it's fully grown (optional cleanup)
        setTimeout(() => {
          setPlants(prev => prev.filter(plant => plant.id !== newPlant.id));
        }, 4000);
      }, 3000);
    }, 5000); // Drop every 5 seconds

    return () => clearInterval(dropInterval);
  }, [dropCounter]);

  return (
    <section className="hero" id="home">
      <FloatingLeaves />

      {/* Lollipop Drop Zone */}
      <div className="lollipop-drop-zone">
        {lollipopDrops.map(drop => (
          <div
            key={drop.id}
            className="dropping-lollipop"
            style={{ left: `${drop.left}%` }}
          />
        ))}

        {/* Sprouting Plants */}
        {plants.map(plant => (
          <div
            key={plant.id}
            className="sprouting-plant"
            style={{ left: `${plant.left}%` }}
          >
            <div className="plant-stem" />
            <div className="plant-leaf leaf-1" />
            <div className="plant-leaf leaf-2" />
          </div>
        ))}
      </div>

      <div className="hero-content fade-in-up">
        <div className="hero-text">
          <h1>
            A Sweet Promise <br/>to Future Gardens
          </h1>
          <p className="hero-subtitle">
            Share a moment. Plant a memory. Grow a legacy together.
          </p>
          <p className="hero-description">
            Picture this: You hand your child an artisanal lollipop... you both savor the sweetness, then together you plant the biodegradable stick... a living reminder of that perfect shared moment, now blooming for generations to come.
          </p>
          <div className="hero-buttons">
            <a href="#shop" className="btn btn-primary">Start Our Tradition</a>
            <a href="#about" className="btn btn-outline">See the Magic</a>
          </div>
        </div>
      </div>
    </section>
  );
};

