import React, { useState, useEffect } from 'react';
import './App.css';

// Header Component
const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <nav className="nav">
          <a href="#home" className="logo">Amborella Organics</a>
          <ul className="nav-links">
            <li><a href="#shop">Shop</a></li>
            <li><a href="#grow">Grow</a></li>
            <li><a href="#wholesale">Wholesale</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
          <a href="#cart" className="cart-icon">ğŸ›’ 0</a>
        </nav>
      </div>
    </header>
  );
};

// Hero Section Component
const Hero = () => {
  return (
    <section className="hero" id="home">
      <div className="hero-content fade-in-up">
        <div className="hero-text">
          <h1>Where Indulgence Blooms Into Legacy</h1>
          <p className="hero-subtitle">
            The world's first seed-bearing confection that transforms fleeting pleasure into living heritage
          </p>
          <p className="hero-description">
            Each exquisite lollipop is a covenant between you and the earthâ€”an organic symphony of 
            heirloom herbs and flowers that awakens your palate, then awakens the soil. When the last 
            taste dissolves, plant the biodegradable stick and watch your moment of indulgence 
            blossom into a garden of shared memories.
          </p>
          <div className="hero-buttons">
            <a href="#shop" className="btn btn-primary">Begin Your Garden</a>
            <a href="#about" className="btn btn-outline">Discover the Story</a>
          </div>
        </div>
        <div className="hero-visual">
          <div className="hero-main-visual">
            ğŸ­
          </div>
          <div className="hero-accent-1">ğŸŒ±</div>
          <div className="hero-accent-2">ğŸŒ¿</div>
          <div className="hero-accent-3">ğŸŒ¸</div>
        </div>
      </div>
    </section>
  );
};

// About Section Component
const About = () => {
  return (
    <section className="about" id="about">
      <div className="container-narrow">
        <div className="about-content">
          <div className="about-text">
            <h2>A New Covenant with Nature</h2>
            <p className="text-luxury">
              In a world hungry for connection, we've created something extraordinaryâ€”a bridge between 
              momentary pleasure and lasting purpose.
            </p>
            <p>
              Each Amborella lollipop begins as an artisanal confection, meticulously crafted with organic 
              herbs and flowers that dance across your palate in unexpected harmonies. But this is merely 
              the prelude to something profound.
            </p>
            <p>
              When sweetness fades and memory lingers, the biodegradable stick reveals its secretâ€”
              embedded within are seeds of the very plants that flavored your experience. Place it 
              horizontally in rich earth, tend it with intention, and witness the miraculous transformation 
              from indulgence to inheritance.
            </p>
            <p className="text-large">
              This is confectionery as <em>ceremony</em>â€”each bite a celebration, each garden a testament 
              to our shared responsibility for the earth's flourishing.
            </p>
          </div>
          <div className="about-visual">
            <div style={{
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '3rem',
              height: '480px'
